class Solution {
public:
    int mod = 1e9+7;
    int f(int idx, int p1, int p2, vector<int>&nums, vector<vector<vector<int>>>&dp){
        if(idx==nums.size()){
            return 1;
        }
        if(dp[idx][p1][p2]!=-1)return dp[idx][p1][p2];
        int res = 0;
        for(int i=p1; i<=50; i++){
            int s = nums[idx] - i;
            if(s>=0&&s<=p2){
                res = (res + f(idx+1, i, s, nums, dp))%mod;
            }
        }
        return dp[idx][p1][p2] = res;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>>dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return f(0, 0, 50, nums, dp);
    }
};
