class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        @cache
        def dp(i, one, two):
            if i == len(nums):
                return 1
            result = 0
            num = nums[i]
            for j in range(num+1):
                if j >= one and num-j <= two:
                    result += dp(i+1, j, num-j) % MOD
            return result % MOD
        return dp(0, 0, nums[0]) % MOD
