class Solution {
public:
    int M= 1e9+7;
    long long solve(vector<int>&nums,long long ind,long long first,long long second,vector<vector<vector<long long>>>&dp){
         if(ind ==nums.size()) return 1;
         long long count=0;
         if( dp[ind][first+1][second]!=-1) return dp[ind][first+1][second];
         for(int i=0;i<=nums[ind];i++){
             int f = i;
             int s = nums[ind]-i;
             if(first<=f && second>=s) {
                 count =  (count %M + solve(nums,ind+1,f,s,dp)%M)%M;
             }
         }
         return dp[ind][first+1][second]=count%M;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<long long>>>dp(nums.size(),vector<vector<long long>>(55,vector<long long>(55,-1)));
        return solve(nums,0,-1,51,dp);
    }
};
