class Solution {
public:
    int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int helper(int ind, int aPrev, int bPrev, vector<int> &nums) {
        if (ind == nums.size()) {
            return 1; 
        }
        if (dp[ind][aPrev][bPrev] != -1) {
            return dp[ind][aPrev][bPrev]; 
        }

        int count = 0;
        for (int i = 0; i <= nums[ind]; i++) {
            int a = i, b = nums[ind] - i;
            if (a >= aPrev && b <= bPrev) {
                count = (count + helper(ind + 1, a, b, nums)) % MOD;
            }
        }

        return dp[ind][aPrev][bPrev] = count;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(101, vector<int>(101, -1)));
        return helper(0, 0, 100, nums);
    }
};
