class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));
        for (int i = 0; i <= nums[0]; i++) {
            int j = nums[0] - i;
            if (j >= 0 && j <= 50) {
                dp[0][i][j] = 1;
            }
        }
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                int k = nums[i] -j;
                if (k < 0 || k > 50) {
                    continue;
                }
                for (int l = 0; l <= nums[i - 1]; l++) {
                    int m = nums[i - 1] - l;
                    if (m < 0 || m > 50) {
                        continue;
                    }
                    if (l <= j && m >= k) {
                        dp[i][j][k] = (dp[i][j][k] + dp[i - 1][l][m]) % MOD;
                    }
                }
            }
        }
        int res = 0;
        for (int i = 0; i <= nums[n - 1]; i++) {
            int j = nums[n - 1] - i;
            if (j >= 0 && j <= 50) {
                res = (res + dp[n - 1][i][j]) % MOD;
            }
        }
        return res;
    }
};
