class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int M = 1e9+7;
        function<int(int,int,int)> dfs, _dfs = [&](int cur, int p, int rp) {
            if (cur == nums.size()) return 1;
            int ret = 0;
            for (int i=p; i<=nums[cur]; i++) {
                if (nums[cur]-i <= rp) {
                    ret = (ret+dfs(cur+1, i, nums[cur]-i))%M;
                }
            }
            return ret;
        };
        vector<vector<int>> memo(2002, vector<int>(52, -1));
        dfs = [&](int cur, int p, int rp) {
            if (memo[cur][p] == -1) memo[cur][p] = _dfs(cur, p, rp);
            return memo[cur][p];
        };
        return dfs(0, 0, INT_MAX);
    }
};
