class Solution {
public:
    vector<vector<vector<int>>> dp;
    int mod = 1e9 + 7;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.assign(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(0, 0, 50, nums);
    }
    int solve(int idx, int prev1, int prev2, vector<int>& nums) {
        if (idx == nums.size()) return 1;

        if (dp[idx][prev1][prev2] != -1) return dp[idx][prev1][prev2];

        long long res = 0;
        for (int i = 0; i <= nums[idx]; i++) {
            int x = nums[idx] - i;
            if (i >= prev1 && x <= prev2) {
                res += solve(idx + 1, i, x, nums);
                res %= mod;
            }
        }

        return dp[idx][prev1][prev2] = res;
    }
};
