class Solution {
public:
    int mod = 1e9+7;
    int dp[2001][51][51];
    int solve(int i, vector<int> &nums, int x, int y)
    {
        if(i >= nums.size()) return 1;
        if(dp[i][x][y] != -1) return dp[i][x][y];
        int ans = 0;
        for(int j=0;j<=nums[i];j++)
        {
            int nx = j;
            int ny = nums[i]-j;
            if(nx >= x and ny <= y)
            {
                ans = (ans + solve(i+1,nums,nx,ny))%mod;
            }
        }
        return dp[i][x][y] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int n = nums.size();
        return solve(0,nums,0,50);
    }
};
