class Solution {
public:
    int mod=1e9+7;
    int res=0;
    vector<vector<vector<int>>>dp;
    int solv(int a,int b,int ind,vector<int>&nums,int n)
    {
        if(ind>=n)
            return 1;
        if(dp[ind][a+1][b]!=-1)
            return dp[ind][a+1][b];
        int curr=0;
        for(int i=0;i<=50;i++)
        {
            if(i>nums[ind])
                break;
            int x=i;
            int y=abs(nums[ind]-x);
            if(x>=a && y<=b)
            {
                curr=(curr+solv(x,y,ind+1,nums,n))%mod;
            }
        }
        return dp[ind][a+1][b]=curr;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        dp.resize(n+1, vector<vector<int>>(55, vector<int>(55, -1)));
        return solv(-1,53,0,nums,nums.size());

    }
};
