class Solution {
    static final int MOD = 1000000007;
    public int countOfPairs(int[] nums) {
    int n = nums.length;
        int maxValue = 50;

        // dp[i][j]: number of ways to fill up to index i with arr1[i] = j
        int[][] dp = new int[n][maxValue + 1];

        // Base case: For the first element, any valid arr1[0] and arr2[0] = nums[0] - arr1[0] is a valid pair
        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
        }

        // Fill the DP table
        for (int i = 1; i < n; i++) {
            int[][] newDp = new int[n][maxValue + 1];
            for (int j = 0; j <= nums[i]; j++) {
                int arr2 = nums[i] - j;
                for (int k = 0; k <= j; k++) {
                    if (arr2 <= nums[i - 1] - k) {
                        newDp[i][j] = (newDp[i][j] + dp[i - 1][k]) % MOD;
                    }
                }
            }
            dp = newDp;
        }

        // Sum up all valid ways for the last element
        int result = 0;
        for (int j = 0; j <= maxValue; j++) {
            result = (result + dp[n - 1][j]) % MOD;
        }

        return result;
    }
}
