class Solution:
    def dfs(self,i,a,b,nums,dp):
        if i>=len(nums):
            return 1
        if a>nums[i]:
            return 0
        if (i,a,b) in dp:
            return dp[(i,a,b)]
        s=0
        for x in range(a,nums[i]+1):
            if nums[i]-x<=b:
                s=(s+self.dfs(i+1,x,nums[i]-x,nums,dp))%(10**9+7)
        dp[(i,a,b)]=s
        return s

    def countOfPairs(self, nums: List[int]) -> int:
        dp={}
        return self.dfs(0,0,50,nums,dp)
