class Solution {
    #define ll long long 
    int MOD=1e9+7;
public:
    ll counter(int ind,int v1,int v2,vector<vector<vector<ll>>> &dp,int n,vector<int>&nums){
        if(ind>=n)return 1;
        ll res=0;
        if(dp[ind][v1][v2]!=-1)return dp[ind][v1][v2];
        for(int i=v1;i<=nums[ind];i++){
            for(int j=v2;j>=0;j--){
                if(i+j==nums[ind]){
                    res+=counter(ind+1,i,j,dp,n,nums);
                    res=res%MOD;
                }
            }
        }
        return dp[ind][v1][v2]=res;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<ll>>> dp(n,vector<vector<ll>>(51,vector<ll>(51,-1)));
        return counter(0,0,nums[0],dp,n,nums);
    }
};
