lli dp[2005][55][55];
lli mod = 1000000000+7;
lli calc(int i,int j,int k,vector<int> &nums)
{
    if(i==nums.size())return 1;
    if(dp[i][j][k]!=-1)return dp[i][j][k];
    int res =0;
    for(int l=0 ;l<=nums[i];l++ )
    {
        if((l>=j && (nums[i]-l)<=k)|| i==0)
        {
            res = (calc(i+1,l,nums[i]-l,nums)+res)%mod;
        }
    }
    dp[i][j][k]=res;
    return res;
}
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return calc(0,0,0,nums);
    }
};
