class Solution
{

    vector<vector<int>> memo;
    vector<int> sol;
    int mod = 1e9 + 7;

    int f(vector<int> &nums, int i, int prevA)
    {

        int n = nums.size();
        if (i == n)
            return 1;

        if (memo[i][prevA] != -1)
        {
            return memo[i][prevA];
        }

        int prevB = nums[i - 1] - prevA;
        int count = 0;

        for (int val1 = prevA; val1 <= nums[i]; val1++)
        {
            int val2 = nums[i] - val1;
            if (val2 <= prevB)
            {
                count = (count + f(nums, i + 1, val1)) % mod;
            }
        }

        return memo[i][prevA] = count;
    }

public:
    int countOfPairs(vector<int> &nums)
    {
        if (nums.size() == 0)
            return 0;
        memo = vector<vector<int>>(nums.size(), vector<int>(51, -1));
        int count = 0;

        for (int val1 = 0; val1 <= nums[0]; val1++)
        {
            count = (count + f(nums, 1, val1)) % mod;
        }

        return count;
    }
};
