class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int MOD = 1e9 + 7;
        vector<vector<int>> memo(n, vector<int>(1001, -1));
        return helper(0, 0, nums, memo, MOD);
    }
    static int helper(int i, int prev, const vector<int>& nums, vector<vector<int>>& memo, int MOD) {
        if (i == nums.size()) return 1;
        if (memo[i][prev] != -1) return memo[i][prev];

        int temp = 0;
        for (int j = prev; j <= nums[i]; ++j) {
            int y = nums[i] - j; // arr2[i] value
            if (i == 0 || y <= (nums[i-1] - prev)) {
                temp = (temp + helper(i + 1, j, nums, memo, MOD)) % MOD;
            }
        }

        return memo[i][prev] = temp;
    }
};
