class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        nn = len(nums)
        mmax = max(nums)
        dp = []
        res = 0
        md = 10**9 + 7
        for left in range(nums[0] + 1):
            right = nums[0] - left
            dp.append([left, right, 1])

        for ii in range(1, nn):
            num = nums[ii]
            nnext_dp = []
            for left in range(0, num + 1):
                right = num - left
                cur = 0
                for p_left, p_right, p_val in dp:
                    if left >= p_left and right <= p_right:
                        cur += p_val % md
                        # print(left, right, cur)
                if cur != 0:
                    nnext_dp.append([left, right, cur])
            # print(dp)
            # print(nnext_dp)
            # print()
            dp = nnext_dp
        for p_left, p_right, num in dp:
            res += num
            res = res % md
        return res
