#define ll long long
const ll MOD = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        int max_a = *max_element(a.begin(), a.end());
        vector<vector<ll>> dp(n, vector<ll>(max_a + 1, -1));

        // dp[i][j] - total number of valid permutations that can be formed by assigning
        // elements from [i...n] such that the last element of arr1 is j

        function<ll(int, int)> f = [&](int i, int j) {
            if (i == n) return 1LL;
            if (dp[i][j] != -1) return dp[i][j];

            ll ans = 0;
            for (int k = j; k <= a[i]; k++) {
                if ((a[i] - k) <= (a[i-1] - j)) {
                    ans = (ans + f(i + 1, k)) % MOD;
                }
            }

            return dp[i][j] = ans;
        };

        ll ans = 0LL;
        for (int i = 0; i <= a[0]; i++) {
            ans = (ans + f(1, i)) % MOD;
        }

        return ans;
    }
};
