class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7

        @cache
        def solve(n, min_so_far, max_so_far):
            if n == len(nums):
                # print(f'n={n}, min_so_far={min_so_far} max_so_far={max_so_far}')
                return 1
            count = 0
            for val in range(max_so_far, nums[n] + 1):
                mini = nums[n] - val
                if mini <= min_so_far:
                    count = (count + solve(n + 1, mini, val)) % MOD
            return count

        return solve(0, 50, 0)
