#define ll long long int
class Solution {
public:
    vector<vector<vector<ll>>> memo;
    ll MOD = 1e9 + 7;

    ll solve(int index, int val1, int val2, vector<int>& values) {
        if (index >= values.size()) return 1;
        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

        ll result = 0;
        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    result += solve(index + 1, i, j, values);
                    result = result % MOD;
                }
            }
        }
        return memo[index][val1][val2] = result;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memo = vector<vector<vector<ll>>>(n, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return solve(0, 0, nums[0], nums);
    }
};
