#define LLI long long int
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int size = nums.size();
        vector<vector<vector<LLI>>> memo(size, vector<vector<LLI>>(51, vector<LLI>(51, -1)));

        function<LLI(int, int, int)> solve = [&](int index, int val1, int val2) -> LLI {
            if (index >= nums.size())
                return 1;
            LLI result = 0;
            LLI mod_val = 1e9 + 7;
            if (memo[index][val1][val2] != -1)
                return memo[index][val1][val2];

            for (int i = val1; i <= nums[index]; i++) {
                for (int j = val2; j >= 0; j--) {
                    if (i + j == nums[index]) {
                        result += solve(index + 1, i, j);
                        result = result % mod_val;
                    }
                }
            }

            return memo[index][val1][val2] = result;
        };
        return solve(0, 0, nums[0]);
    }
};
