class Solution {
public:
    int n;
    const int MOD = 1e9+7;
    vector<vector<vector<int>>> dp;
    int f(int ind, int prev1, int prev2, vector<int> &nums){
        if(ind==n) return 1;
        if(dp[ind][prev1][prev2]!=-1) return dp[ind][prev1][prev2];
        int val = nums[ind];
        int ans{};
        for(int k{}; k<=val; k++){
            if(k>=prev1 && val-k<=prev2){
                ans = (ans + f(ind+1,k,val-k,nums))%MOD;
            }
        }
        return dp[ind][prev1][prev2] = ans%MOD;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        dp.resize(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return f(0,0,nums[0],nums);
    }
};
