class Solution {
public:
    int mod=1e9+7;

    long long f(vector<int>&nums,int prev1,int idx,vector<vector<long long>> &dp){
        if(idx==nums.size()){
            return 1;
        }
        if(dp[prev1][idx]!=-1) return dp[prev1][idx];
        int res=0;
        for(int i=prev1;i<=nums[idx];i++){
            if(idx==0 or nums[idx]-i<=nums[idx-1]-prev1){
               res=(res+f(nums,i,idx+1,dp))%mod; 
            }
        }
        return dp[prev1][idx]=res;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<long long>> dp(51,vector<long long>(n,-1));
        return f(nums,0,0,dp);
    }
};
