using ll = long long;

class Solution {
public:
    ll findCombinations(int index, int firstValue, int secondValue, vector<int>& numbers, vector<vector<vector<ll>>>& memo) {
        if (index >= numbers.size()) return 1;
        ll combinations = 0;
        ll mod = 1e9 + 7;
        if (memo[index][firstValue][secondValue] != -1) return memo[index][firstValue][secondValue];
        for (int i = firstValue; i <= numbers[index]; i++) {
            for (int j = secondValue; j >= 0; j--) {
                if (i + j == numbers[index]) {
                    combinations += findCombinations(index + 1, i, j, numbers, memo);
                    combinations %= mod;
                }
            }
        }
        return memo[index][firstValue][secondValue] = combinations;
    }

    int countOfPairs(vector<int>& numbers) {
        int n = numbers.size();
        vector<vector<vector<ll>>> memo(n, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return findCombinations(0, 0, numbers[0], numbers, memo);
    }
};
