MOD = 10**9 + 7

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        cache = {}

        def dfs(i, prev1, prev2):
            if i == len(nums):
                return 1

            if (i, prev1, prev2) in cache:
                return cache[(i, prev1, prev2)]

            total = 0
            for x in range(prev1, nums[i] + 1):
                y = nums[i] - x
                if y >= 0 and y <= prev2:
                    total = (total + dfs(i + 1, x, y)) % MOD

            cache[(i, prev1, prev2)] = total
            return total

        return dfs(0, 0, float("inf"))
