class Solution {
private:
    const int mod = 1e9 +7;

    long long f(int i, int prev, vector<int>& nums, vector<vector<int>>& dp) {
        if(i >= nums.size()) return 1LL;

        if(dp[i][prev] != -1) return dp[i][prev];
        long long ans = 0;
        int dprev = nums[i-1]-prev;
        for(int j = prev; j <= nums[i]; j++) {
            if(dprev >= nums[i]-j) ans = (ans+(f(i+1, j, nums, dp)%mod))%mod;
        }
        return dp[i][prev] = ans;
    }

public:
    int countOfPairs(vector<int>& nums) {
        long long res = 0, n = nums.size();
        int maxi = *max_element(nums.begin(), nums.end());

        vector dp(n, vector(maxi+1, -1));
        for(int i = 0; i <= nums[0]; i++) res = (res+(f(1, i, nums, dp)%mod))%mod;

        return res;
    }
};
