class Solution {
public:

       using vyas = long long;
       vyas answer(int i, int j, int k, vector<int>& nums, vector<vector<vector<vyas>>>& shivam) {
        if (i >= nums.size()) return 1; 
        vyas bhoovesh = 1e9 + 7;
        if (shivam[i][j][k] != -1) return shivam[i][j][k]; 
        vyas answeraagyabhai = 0;
        for (int x = j; x <= nums[i]; x++) {  
            int y = nums[i] - x;  
            if (y <= k && y >= 0) { 
                answeraagyabhai += answer(i + 1, x, y, nums, shivam);
                answeraagyabhai %= bhoovesh;
            }
        }
        return shivam[i][j][k] = answeraagyabhai; 
    }
    int countOfPairs(vector<int>& nums) {
      int n = nums.size();
        vector<vector<vector<vyas>>> shivam(n, vector<vector<vyas>>(51, vector<vyas>(51, -1)));
        return answer(0, 0, nums[0], nums, shivam);   

    }
};
