#include "bits/stdc++.h"
using namespace std;

typedef long long ll;
typedef long double ld;
const int MN=1e5+10;
const int MN2=51;
const int MOD=1e9+7;
const int bits=31;
using pi = pair<ll, ll>;
using ti = tuple<ll, ll, ll>;
#define pb push_back
#define mp make_pair
#define mt make_tuple
#define MAX LLONG_MAX
#define MIN LLONG_MIN
#define MAX_SIZE 1000005
#define lcm(a, b) (a*b)/__gcd(a, b)

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n=nums.size();
        ll dp[MN2]={0};
        for (int j=0; j<=nums[0]; j++){
            dp[j]=1;
        }
        for (int i=1; i<n; i++){
            ll dp2[MN2]={0};
            vector<ll> prefix(MN2+1, 0);
            for (int j=0; j<MN2; j++){
                prefix[j]=dp[j];
                prefix[j]%=MOD;
                if (j>0){
                    prefix[j]+=prefix[j-1];
                    prefix[j]%=MOD;
                }
            }
            for (int j=0; j<=nums[i]; j++){
                ll curr=j;
                ll curr2=nums[i]-j;
                ll r=min(curr, nums[i-1]-curr2);
                if (r>=0){
                    dp2[j]+=prefix[r];
                    dp2[j]%=MOD;
                }
            }
            for (int j=0; j<MN2; j++){
                dp[j]=dp2[j];
            }
        }
        ll ans=0;
        for (int i=0; i<MN2; i++){
            ans+=dp[i];
            ans%=MOD;
        }
        return ans;
    }
};
