const int MOD = 1e9 + 7;
class Solution {
private:
    int solve(int idx, int prev, vector<int>& nums,vector<vector<int>>& dp) {
        if(idx == nums.size()) {
            return 1;
        }
        if(idx != 0 && dp[idx][prev] != -1) {
            return dp[idx][prev];
        }
        int prev1, prev2;
        if(idx == 0) {
            prev1 = 0;
            prev2 = INT_MAX;
        }
        else {
            prev1 = prev;
            prev2 = nums[idx-1] - prev;
        }
        long long ans = 0;
        for(int i=0;i<=nums[idx]/2;i++) {
            int num1 = i;
            int num2 = nums[idx]-i;
            if(num1 >= prev1 && num2 <= prev2) {
                ans = (ans + solve(idx+1,num1, nums,dp))%MOD;
            }
            if(num2 >= prev1 && num1 <= prev2 && num1 != num2) {
                ans = (ans + solve(idx+1,num2, nums,dp))%MOD;
            }
        }
        if(idx != 0) dp[idx][prev] = ans;
        return (int) ans;
    }

public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size(),vector<int>(51,-1));
        return solve(0,-1,nums,dp);
    }
};
