#include <vector>
#include <cstdint>

class Solution {
    using ll = int64_t;
    static constexpr ll MOD = 1e9 + 7;
    std::vector<int> nums;
    std::vector<std::vector<std::vector<ll>>> dp;

    ll count(int i, int v1, int v2) {
        if (i == nums.size()) return 1;
        if (dp[i][v1][v2] != -1) return dp[i][v1][v2];

        ll res = 0;
        for (int x = v1; x <= nums[i]; ++x)
            for (int y = std::min(v2, nums[i] - x); y >= 0; --y)
                if (x + y == nums[i])
                    res = (res + count(i + 1, x, y)) % MOD;

        return dp[i][v1][v2] = res;
    }

public:
    int countOfPairs(std::vector<int>& numbers) {
        nums = numbers;
        dp.assign(nums.size(), std::vector<std::vector<ll>>(51, std::vector<ll>(51, -1)));
        return static_cast<int>(count(0, 0, nums[0]));
    }
};
