class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 7 + 10**9
        @cache
        def helper(index,start,last):
            if index==len(nums):
                return 1
            if nums[index]<start:
                return 0
            ans = 0
            for value in range(start,nums[index]+1):
                if nums[index]-value<=last:
                    ans+=helper(index+1,value,nums[index]-value)
                    ans%=MOD
            return ans
        return helper(0,0,math.inf)
