class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        int dp[2][51][51] = {0};
        int cur = 0, prev = 1;

        for (int i = 0; i <= nums[0]; ++i) {
            dp[cur][i][nums[0] - i] = 1;
        }

        for (int i = 1; i < n; ++i) {
            swap(cur, prev);
            memset(dp[cur], 0, sizeof(dp[cur]));

            int cumSum[51][51] = {0};

            for (int x = 0; x <= 50; ++x) {
                for (int y = 0; y <= 50; ++y) {
                    cumSum[x][y] = dp[prev][x][y];
                    if (x > 0) cumSum[x][y] = (cumSum[x][y] + cumSum[x - 1][y]) % MOD;
                    if (y > 0) cumSum[x][y] = (cumSum[x][y] + cumSum[x][y - 1]) % MOD;
                    if (x > 0 && y > 0) cumSum[x][y] = (cumSum[x][y] - cumSum[x - 1][y - 1] + MOD) % MOD;
                }
            }

            for (int x = 0; x <= nums[i]; ++x) {
                int y = nums[i] - x;
                dp[cur][x][y] = cumSum[x][50] - (y > 0 ? cumSum[x][y - 1] : 0) + MOD;
                dp[cur][x][y] %= MOD;
            }
        }

        int res = 0;
        for (int x = 0; x <= 50; ++x) {
            for (int y = 0; y <= 50; ++y) {
                res = (res + dp[cur][x][y]) % MOD;
            }
        }

        return res;
    }
};
