class Solution {

    int[][][] memo;

    public int countOfPairs(int[] nums) {

        memo = new int[nums.length][51][51];

        for (int i = 0; i < nums.length; i++) {
            for (int j = 0; j <= 50; j++) {
                for (int k = 0; k <= 50; k++) {

                    memo[i][j][k] = -1;
                }
            }
        }

        int index = 0;
        int lastValueOfArr1 = 0;
        int lastValueOfArr2 = 50;

        return solve(nums, index, lastValueOfArr1, lastValueOfArr2);
    }
    public int solve(int[] nums, int index, int lastValueOfArr1, int lastValueOfArr2){

        if(index == nums.length){

            return 1;
        }

        if(memo[index][lastValueOfArr1][lastValueOfArr2] != -1){

            return memo[index][lastValueOfArr1][lastValueOfArr2];
        }

        int pairCount = 0;

        for(int arr1=lastValueOfArr1; arr1<=nums[index]; arr1++){

            int arr2 = nums[index] - arr1;

            if(arr2 <= lastValueOfArr2){

                pairCount = (pairCount + solve(nums, index + 1, arr1, arr2)) % 1000000007;
            }
        }


        return memo[index][lastValueOfArr1][lastValueOfArr2] = pairCount;
    }
}
