class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        ans = 0
        dp = {}
        def brute(i, arr1, arr2):
            if i == n:
                nonlocal ans
                ans += 1
                return 1
            if (i, arr1, arr2) in dp:
                return dp[(i, arr1, arr2)]
            if i == 0:
                op = 0
                for val in range(nums[0] + 1):
                    op += brute(i + 1, val, nums[0] - val)
                dp[(i, arr1, arr2)] = op
                return op
            op =0 
            for val in range(nums[i] + 1):
                if arr1 <= val and arr2 >= nums[i] - val:
                    op += brute(i + 1, val, nums[i] - val)
            dp[(i, arr1, arr2)] = op
            return op
        return brute(0, None, None) % 1000000007
