class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n=len(nums)
        @cache
        def dp(i,mina,maxb):
            if i==n:
                print()
                return 1
            ans=0
            for a in range(mina,nums[i]+1):
                b=nums[i]-a
                if b<=maxb:
                    ans+=dp(i+1,a,b)
            return ans%(10**9+7)
        return dp(0,0,max(nums))
