class Solution {
public:

    vector<int > DP[55];
    int MOD = 1e9 + 7;
    int solve(int last1, int curIdx, vector<int > &nums) {
        int N = nums.size();

        if( curIdx >= N ) {
            // cout<<"here"<<"\n";
            return 1;
        }

        if( DP[last1][curIdx] != -1 ) {
            return DP[last1][curIdx];
        }

        int last2 = ( curIdx >= 1 ? nums[curIdx - 1] - last1 : 60 );

        // cout<<last1<<", "<<last2<<"-"<<curIdx<<"\n";
        int ans = 0;
        for(int first=last1; first<=nums[curIdx]; first++) {    
            int second = nums[curIdx] - first;

            if( second <= last2 ) {
                ans = (ans + solve(first, curIdx + 1, nums)) % MOD;
            }
        }

        return DP[last1][curIdx] = ans % MOD;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        for(int i=0; i<55; i++) {
            DP[i].resize(n, -1);
        }

        return solve(0, 0, nums);
    }
};
