class Solution {
    public int countOfPairs(int[] nums) {
        int M = 1_000_000_007;
        int l = nums.length;
        int[][] d = new int[51][51];        
        for (int x = 0; x <= 50; x++) {
            int y = nums[0] - x;
            if (0 <= y && y <= 50) {
                d[x][y] = 1;
            }
        }        
        for (int i = 1; i < l; i++) {
            int[][] e = new int[51][51];
            int[][] p = new int[51][51];            
            for (int x = 0; x <= 50; x++) {
                int s = 0;
                for (int y = 50; y >= 0; y--) {
                    s = (s + d[x][y]) % M;
                    p[x][y] = s;
                }
            }            
            for (int x = 0; x <= 50; x++) {
                int y = nums[i] - x;
                if (0 <= y && y <= 50) {
                    for (int k = 0; k <= x; k++) {
                        e[x][y] = (e[x][y] + p[k][y]) % M;
                    }
                }
            }            
            d = e;
        }        
        int result = 0;
        for (int[] row : d) {
            for (int val : row) {
                result = (result + val) % M;
            }
        }        
        return result;
    }
}
