class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7
        @cache
        def dp(i,L,U):
            if i == n:
                return 1
            ans = 0
            for x in range(max(L,nums[i]-U),nums[i]+1):
                #if i == 0: print(i,x)
                ans += dp(i + 1, x,nums[i] - x) % MOD

            return ans % MOD
        return dp(0,0,50)
