  unordered_map<int, unordered_map<int, unordered_map<int, long long>>> cache;
  long long modulo = 1000000007;

  long long getCount(vector<int>& nums, int idx, int lb, int ub) {
    int n = nums.size();
    if (idx >= n) return 1;
    if (cache[idx][lb].count(ub)) {
      return cache[idx][lb][ub];
    }
    long long tot = 0;
    int val = nums[idx];
    //cout << "Val = " << val << ", lb = " << lb << ", ub = " << ub << endl;
    int valLb = lb, valUb = ub;
    if (valLb + valUb < val) {
      valLb += (val - (valLb + valUb));
      //if (valLb > ub) return 0;
    }
    if (valLb + valUb > val) {
      valUb += (val - (valLb + valUb));
      //if (valLb < lb) return 0;
    }
    //int valLb = lb, valUb = ub;
    //if ((val - ub) < lb && val - lb > ub) return 0;
    //if ((val - ub) >= lb) {
    //int valLb = val - ub, valUb = ub;
    //cout << "Val = " << val << ", idx = " << idx << ", valLb = " << valLb << ", valUb = " << valUb << endl;
    //while (valLb <= valUb) {
    while (valLb <= 50 && valUb >= 0) {
      tot += getCount(nums, idx + 1, valLb, valUb);
      tot %= modulo;
      valLb++;
      valUb--;
    }
    //}
    //if ((val - lb) <= ub) {
