class Solution {


    public int countOfPairs(int[] nums) {

        int mod = 1000_000_007;

        // 感觉像dp
        int n = nums.length;
        int[][] dp = new int[n][51];


        // 最后一个位置显然都是1
        for(int i=0; i<= nums[n-1]; i++){
            dp[n-1][i] = 1;
        }

        // 从后往前
        for(int idx = n-2; idx>=0; idx--){
            // 计算每个位置
            int value = nums[idx];
            int after = nums[idx+1];
            for(int i=0; i<= value; i++){
                int value1 = i;
                int value2 = value - value1;
                for(int j=after; j>=0; j--){
                    int after1 = j;
                    int after2 = after - after1;
                    if(after1 < value1){
                        break;
                    }
                    if(after2 > value2){
                        break;
                    }
                    // 满足条件
                    dp[idx][value1] = (dp[idx][value1] + dp[idx+1][after1] ) % mod;
                }
            }
        }

        int sum = 0;
        for(int i=0; i<=50; i++){
            sum = (sum + dp[0][i]) % mod;
        }
        return sum;
    }

}
