class Solution {
public:
 int countOfPairs(vector<int>& nums) {
    int n = nums.size();
        vector<vector<vector<long long>>> v(n, vector<vector<long long>>(51, vector<long long>(51, -2)));
        const long long MOD = 1e9 + 7;

        function<long long(int, int, int)> solve = [&](int index, int a, int b) -> long long {
            if (index == n) return 1;

            if (v[index][a][b] != -2) return v[index][a][b];

            long long ans = 0;
            for (int i = a; i <= nums[index]; i++) {
                int j = nums[index] - i;
                if (j <= b) {
                    ans += solve(index + 1, i, j);
                    ans %= MOD;
                }
            }

            return v[index][a][ b] = ans;
        };

        return solve(0, 0, 50); 
    }



};
