class Solution {
public:
#define ll long long
#define mod_value 1000000007


bool valid_pair(int first, int second, int sum) {
    return (first + second == sum);
}


ll process_second(int position, int first, int maximum, vector<int>& numbers, vector<vector<vector<ll>>>& dp) {
    ll inner_result = 0;
    for (int second = maximum; second >= 0; second--) {
        if (valid_pair(first, second, numbers[position])) {
            inner_result += combination_count(position + 1, first, second, numbers, dp);
            inner_result %= mod_value;
        }
    }
    return inner_result;
}


ll process_first(int position, int minimum, int maximum, vector<int>& numbers, vector<vector<vector<ll>>>& dp) {
    ll outer_result = 0;
    for (int first = minimum; first <= numbers[position]; first++) {
        outer_result += process_second(position, first, maximum, numbers, dp);
        outer_result %= mod_value;
    }
    return outer_result;
}

ll combination_count(int position, int minimum, int maximum, vector<int>& numbers, vector<vector<vector<ll>>>& dp) {
    if (position >= numbers.size()) return 1;
    if (dp[position][minimum][maximum] != -1) return dp[position][minimum][maximum];

    ll result = process_first(position, minimum, maximum, numbers, dp);
    return dp[position][minimum][maximum] = result;
}


int countOfPairs(vector<int>& numbers) {
    int size = numbers.size();
    vector<vector<vector<ll>>> dp(size, vector<vector<ll>>(52, vector<ll>(52, -1)));
    return combination_count(0, 0, numbers[0], numbers, dp);
}

};
