class Solution {
public:
    int dp[2005][55][55];
    int mod = 1e9 + 7;
    long long dfs(vector<int>& nums, int idx, int prevD, int prevI){
        if(idx==nums.size()){
            return 1;
        }

        if(dp[idx][prevD][prevI+1]!=-1){
            return dp[idx][prevD][prevI+1];
        }

        long long ans = 0;
        for(int i=0;i<=50;i++){
            for(int j=0;j<=50;j++){
                if(i<=prevD && j>=prevI && i+j==nums[idx]){
                    ans = (ans + dfs(nums, idx+1, i, j))%mod;
                }
            }
        }
        return dp[idx][prevD][prevI+1] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        int ans = dfs(nums, 0, 51, -1);
        return ans;
    }
};
