MOD = 10**9+7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def can(i, a1, a2):
            if i >= len(nums):
                return 1
            val = nums[i]
            r=0
            for v in range(val+1):
                o = val - v
                x, y = o, v
                if a1 <= x and a2 >= y:
                    r += can(i + 1, x, y)
                    r %= MOD
            return r % MOD
        r = 0
        for v in range(nums[0]+1):
            x, y = v, nums[0] - v
            r += can(1, x, y)
            r %= MOD
        can.cache_clear()
        return r
