const int MOD = 1e9 + 7;
class Solution {
    int n;
    int dp[2001][51][51];
    int f(int i, int a, int b, vector<int>& nums) {
        if (i == n) return 1;
        if (dp[i][a][b] != -1) return dp[i][a][b];

        int res = 0;
        for (int nextA = a; nextA <= nums[i]; nextA++) {
            int nextB = nums[i] - nextA;
            if (nextB <= b) {
                res += f(i + 1, nextA, nextB, nums);
                res %= MOD;
            }
        }

        return dp[i][a][b] = res;
    }

public:
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        memset(dp, -1, sizeof(dp));

        int ans = 0;
        for (int a = 0; a <= nums[0]; ++a) {
            int b = nums[0] - a;
            ans += f(1, a, b, nums);
            ans %= MOD;
        }

        return ans;
    }
};
