int dp[2001][52];
class Solution {
public:
    Solution(){
        memset(dp,-1,sizeof(dp));
    }
    int f(vector<int> &nums,int ind,int pa,int pb)
    {  long long mod=1e9+7;
        if(ind==nums.size())
        return 1;
         int ans=0;
         for(int i=0;i<=nums[ind];i++)
         {
            if(i>=pa && nums[ind]-i<=pb)
            {
                if(dp[ind][i]==-1)
                dp[ind][i]=f(nums,ind+1,i,nums[ind]-i);
                 ans=(ans+dp[ind][i])%mod;
            }
         }
         return ans;

    }
    int countOfPairs(vector<int>& nums) {
        return f(nums,0,0,INT_MAX);
    }
};
