#define ll long long int

class Solution {
public:
    const int MOD = 1e9 + 7;

    ll solve(vector<int>& nums, vector<vector<vector<ll>>>& dp, int ind, int preva, int prevb) {
        if (ind == nums.size()) return 1;

        if (dp[ind][preva][prevb] != -1) return dp[ind][preva][prevb];

        ll result = 0;

        for (int a = preva; a <= nums[ind]; a++) {
            int b = nums[ind] - a;
            if (b > prevb) continue; 

            result = (result + solve(nums, dp, ind + 1, a, b)) % MOD;
            result%=MOD;
        }

        return dp[ind][preva][prevb] = result%MOD;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return solve(nums, dp, 0, 0, 50); 
    }
};
