class Solution {
public:
    int M = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {

        int l = nums.size();
        vector<int> dp(51);
        dp[0] = 1;
        for (int i = 0; i < l; i++) {
            vector<int> dp2(51);
            for (int j = 0; j <= nums[i]; j++) {
                int x = j, y = nums[i] - j;
                int p = i == 0 ? 50 : nums[i - 1];
                for (int k = 0; k <= p; k++) {
                    if (x >= k && y <= p - k && y >= 0) {
                        // if (dp[k]) cout <<i << ", " << k<< " " << p - k << " " << x << " " << y << endl;
                        dp2[j] += dp[k];
                        dp2[j] %= M;
                    }
                }
            }
            swap(dp, dp2);
        }
        return accumulate(dp.begin(), dp.end(), 0l) % M; 
    }

};
