class Solution {
public:

const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
      int n = nums.size();
    int max_num = *max_element(nums.begin(), nums.end());

    vector<vector<int>> dp(n, vector<int>(max_num + 1, 0));
    for (int j = 0; j <= nums[0]; ++j) {
        dp[0][j] = 1;
    }

    for (int i = 1; i < n; ++i) {
        vector<int> new_dp(nums[i] + 1, 0);

        for (int j = 0; j <= nums[i]; ++j) {
            int arr2_i = nums[i] - j;

            for (int k = 0; k <= min(nums[i-1], j); ++k) {
                int arr2_i_minus_1 = nums[i-1] - k;

                if (arr2_i <= arr2_i_minus_1) {
                    new_dp[j] = (new_dp[j] + dp[i-1][k]) % MOD;
                }
            }
        }

        dp[i] = new_dp;
    }

    int result = 0;
    for (int j = 0; j <= nums[n-1]; ++j) {
        result = (result + dp[n-1][j]) % MOD;
    }

    return result;
    }
};
