class Solution {
public:

    long long maxv=1e9+7;

    long long solve(vector<int>& nums,int i,int prev,vector<vector<int>>& dp){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][prev+1]!=-1){
            return dp[i][prev+1]%maxv;
        }
        int a=0;
        for(int c=0;c<=nums[i];c++){
            if(prev==-1){
                a+=solve(nums,i+1,c,dp);
                a=a%maxv;
            }
            else{
                if(c>=prev && nums[i]-c<=nums[i-1]-prev){
                    a+=solve(nums,i+1,c,dp);
                    a=a%maxv;
                }
            }
        }
        return dp[i][prev+1]=a%maxv;
    }

    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size()+1,vector<int>(52,-1));
        return solve(nums,0,-1,dp);
    }
};
