class Solution {
    public int countOfPairs(int[] nums) {
        t=new Long[nums.length][51];
        return (int)(rec(0,-1,nums)%mod);   
    }
    long mod=(int)1e9+7;
    Long[][] t;
    public long rec(int i,int prev,int[] arr){
        if(i>=arr.length) return 1;
        if(prev!=-1 && t[i][prev]!=null) return t[i][prev];
        int prev1=prev==-1?Integer.MIN_VALUE:prev;
        int prev2=prev==-1?Integer.MAX_VALUE:arr[i-1]-prev;
        int n=arr[i];
        long ans=0;
        for(int j=Math.max(0,prev1);j<=n;j++){
            int a=j;
            int b=n-j;
            if(prev1<=a && prev2>=b){
                ans=(ans%mod+rec(i+1,a,arr)%mod)%mod;
            }
        }
        if(prev!=-1) t[i][prev]=ans;
        return ans;
    }
}
